import React, { Component } from 'react'

export class Prices extends Component {
    state = {
        currency : 'USD'
    }

    render() {
        let list = ''

        if(this.state.currency === 'USD'){
            list =  <li className="list-group-item">
            Bitcoin rate for {this.props.bpi.USD.description} 
            : <span class="badge badge-danger">{this.props.bpi.USD.code}</span>
             <strong>&nbsp;{this.props.bpi.USD.rate}</strong> 
        </li>
        }else  if(this.state.currency === 'GBP'){
            list =  <li className="list-group-item">
            Bitcoin rate for {this.props.bpi.GBP.description} 
            : <span class="badge badge-danger">{this.props.bpi.GBP.code}</span>
             <strong>&nbsp;{this.props.bpi.GBP.rate}</strong> 
        </li>
        }else  if(this.state.currency === 'EUR'){
            list =  <li className="list-group-item">
            Bitcoin rate for {this.props.bpi.EUR.description} 
            : <span class="badge badge-danger">{this.props.bpi.EUR.code}</span>
             <strong>&nbsp;{this.props.bpi.EUR.rate}</strong> 
        </li>
        }

        return (
            <div>
                <ul className="list-group">
                    <li className="list-group-item">
                        Bitcoin rate for {this.props.bpi.USD.description} 
                        : <span class="badge badge-danger">{this.props.bpi.USD.code}</span>
                         <strong>&nbsp;{this.props.bpi.USD.rate}</strong> 
                    </li>

                </ul>
            </div>
        )
    }
}

export default Prices
